import
  unittest2,
  stint,
  stew/byteutils,
  web3,
  ../validate_proof

suite "Merkle proof of inclusion validation":
  test "Validate account proof":
    # Valid inclusion proof for account 0xf36f155486299ecaff2d4f5160ed5114c1f66000
    # at execution block 7533830 of goerli network
    let
      stateRoot = FixedBytes[32].fromHex("0x4cc43abefcb010e4176e82e44eadaa49a249d258867ba31f5c14d6099790a614")
      codeHash = FixedBytes[32].fromHex("0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470")
      storageRoot = FixedBytes[32].fromHex("0x56e81f171bcc55a6ff8345e692c0f86e5b48e01b996cadc001622fb5e363b421")
      nonce = Quantity(uint64(71518))
      balance =  UInt256.fromHex("3d25780abb5f0a89b7da")
      address = Address(hexToByteArray[20]("0xf36f155486299ecaff2d4f5160ed5114c1f66000"))
      rlpNodes = @[
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("0xf8749e2070b0cf62febcfb17abd5e2189b6e0029e0f9b9a1aabf0e670469d6ab74b853f8518301175e8a3d25780abb5f0a89b7daa056e81f171bcc55a6ff8345e692c0f86e5b48e01b996cadc001622fb5e363b421a0c5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470"))
      ]

    check:
      getAccountFromProof(
        stateRoot,
        address,
        balance,
        nonce,
        codeHash,
        storageRoot,
        rlpNodes
      ).isOk()

  test "Validate storage proof":
    let slotValue = UInt256.fromHex("0x25a92a5853702f199bb2d805bba05d67025214a8")
    let stateRoot = FixedBytes[32].fromHex("0x99b31961b56190853d6f20b17298c8e3aed0a88860f80d4662fb41712f93491d")
    let proof = ProofResponse(
      address: Address(hexToByteArray[20]("0x805fe47d1fe7d86496753bb4b36206953c1ae660")),
      accountProof: @[
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("")),
        RlpEncodedBytes(hexToSeqByte("0xf8679e20da5951bceaed385c03546f45f276b83d86bc0755e045f92f1d9071f431b846f8440180a07bb85da974b0ee4efcb379f528bcf7e947a55901d5a2c0d38bc9cc16c851e785a03b45ab254ec24f2bcb75a922f15031796bc433ea5a4514783705d185321e5f82"))
      ],
      balance: UInt256.fromHex("0x0"),
      codeHash: FixedBytes[32].fromHex("0x3b45ab254ec24f2bcb75a922f15031796bc433ea5a4514783705d185321e5f82"),
      nonce: Quantity(uint64(1)),
      storageHash: FixedBytes[32].fromHex("0x7bb85da974b0ee4efcb379f528bcf7e947a55901d5a2c0d38bc9cc16c851e785"),
      storageProof: @[
        StorageProof(
          key: u256(0),
          value: slotValue,
          proof: @[
            RlpEncodedBytes(hexToSeqByte("")),
            RlpEncodedBytes(hexToSeqByte("0xf7a0390decd9548b62a8d60345a988386fc84ba6bc95484008f6362f93160ef3e563959425a92a5853702f199bb2d805bba05d67025214a8"))
          ]
        )
      ]
    )

    let validationResult = getStorageData(stateRoot, u256(0), proof)

    check:
      validationResult.isOk()
      validationResult.get == slotValue
