import 
  stew/byteutils,
  ../execution_chain/evm/secp256r1verify

proc validate(input: string) =
  let data = hexToByteArray[160](input)
  discard p256verify(data)
  
const
  xp5ymin  = ""
  xp5yplus = ""
  xpymin   = "" 
  xpyplus  = ""
  yp1 = ""

validate(xp5ymin)
validate(xp5yplus)
validate(xpymin)
validate(xpyplus)
validate(yp1)

